document.addEventListener("DOMContentLoaded",()=>{document.querySelector(".header").addEventListener("mousemove",(function(e){[...this.querySelectorAll(".parallax")].forEach(t=>{const r=t.dataset.speed,s=e.clientX*r/1e3;t.style.transform=`translateX(${s}px)`})})),(()=>{const e=document.querySelector(".burger"),t=document.querySelector(".header-nav-right");e.addEventListener("click",()=>{t.classList.toggle("navLinks-active"),e.classList.toggle("burger-right")}),document.addEventListener("click",r=>{t.classList[3]&&r.target!==e&&(t.classList.remove("navLinks-active"),e.classList.remove("burger-right"))})})()});