const navOpen=()=>{const e=document.querySelector(".burger"),t=document.querySelector(".header-nav-right");e.addEventListener("click",()=>{t.classList.toggle("navLinks-active"),e.classList.toggle("burger-right")})};function parallaxEffect(e){[...this.querySelectorAll(".parallax")].forEach(t=>{const a=t.dataset.speed,r=e.clientX*a/1e3;t.style.transform=`translateX(${r}px)`})}document.querySelector(".header").addEventListener("mousemove",parallaxEffect),navOpen();