document.addEventListener("DOMContentLoaded",()=>{document.querySelector(".header").addEventListener("mousemove",(function(e){[...this.querySelectorAll(".parallax")].forEach(t=>{const r=t.dataset.speed,n=e.clientX*r/1e3;t.style.transform=`translateX(${n}px)`})})),(()=>{const e=document.querySelector(".burger"),t=document.querySelector(".header-nav-right");e.addEventListener("click",()=>{t.classList.toggle("navLinks-active"),e.classList.toggle("burger-right")})})()});